import React from 'react';
import Axios from 'axios';

export default class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: ''
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit(event) {
        Axios.post('/api/v1/users/login', this.state)
            .then(() =>  this.props.getUserData())
            .catch((err) => err.response.data.message? alert(err.response.data.message) : alert('something went wrong'))
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} className="landingForm">
                <label>
                    Username:
            <input type="text" name='username' value={this.state.value} onChange={this.handleChange} required/>
                </label>
                <br />
                <label>
                    Password:
            <input type="password" name='password' value={this.state.value} onChange={this.handleChange} required/>
                </label>
                <br />
                <input className="submitBtn" type="submit" value="Login" />
            </form>
        );
    }
}
